+
+/**
+ * Will realloc *queries to double the size of the current count.
+ */
+static bool q_grow(PENDING_QUERIES *queries)
+{
+    size_t size = queries->count + 2;
+    P_QUERY *new = realloc(queries->query_list, size * sizeof(P_QUERY));
+
+    if (!new) {
+        return false;
+    }
+
+    queries->size  = size;
