+    uint8_t packet[QUERY_PKT_ENCRYPTED_SIZE(send->length)];
+
+    size_t written_size = q_build_pkt(send->key, dht->self_public_key, dht->self_secret_key, NET_PACKET_DATA_REQUEST,
+                                      send->name, send->length, packet);
