+bool group_mute_self(uint32_t groupnumber)
+{
+    GroupChat *chat = &groupchats[groupnumber];
+
+    if (!chat->active || !chat->audio_enabled) {
+        return false;
+    }
+
+    device_mute(input, chat->audio_in_idx);
+
+    return true;
+}
